import {
    REGISTER_SUCCESS,
    LOGIN_SUCCESS,
    LOGIN_FAIL,
    REGISTER_FAIL,
    LOAD_USER,
    AUTH_ERROR
} from './constants/constants';

const initialState = {
    token: localStorage.getItem('token'),
    isAllowedToLogin: false,
    errors: {}
}

const authReducer = (state = initialState, action) => {
    const { type, payload } = action;
    switch(type) {
        default:
            return state;
    }
}

export default authReducer;
