import React, { Component, useState } from 'react'
import { compose, withProps } from 'recompose'
import { withScriptjs, withGoogleMap, GoogleMap, Marker } from 'react-google-maps'

import QuizIcon from '../map/assets/quiz-icon.svg'
import FactIcon from '../map/assets/picture-icon.svg'
import GameIcon from '../map/assets/game-icon.svg'

const [ selectedInteraction, setSelectedInteraction ] = useState(null);

const MapComponent = compose(
    withProps({
        googleMapURL: `https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=${process.env.REACT_APP_GOOGLE_KEY}`,
        loadingElement: <div style={{ height: `100%` }} />,
        containerElement: <div style={{ height: `100vh` }} />,
        mapElement: <div style={{ height: `100%` }} />,
    }),
    withScriptjs,
    withGoogleMap
)((props) =>
    <GoogleMap
        defaultZoom={25}
        center={{ lat: props.currentLocation.lat, lng: props.currentLocation.lng }}
    >
        {props.isMarkerShown &&
        <Marker
            position={{ lat: props.currentLocation.lat, lng: props.currentLocation.lng }}
            onClick={props.onMarkerClick}
        />
        }
        {MapInteractions.location.map((clickableInteraction) => (
            <Marker
                key={clickableInteraction.id}
                position={{
                    lat: clickableInteraction.coordinates[0],
                    lng: clickableInteraction.coordinates[1]
                }}
                onClick={() => {
                    setSelectedInteraction(clickableInteraction);
                }}
                icon={
                    clickableInteraction.type === "Quiz" && ( QuizIcon )
                    ||
                    clickableInteraction.type === "Fact" && ( FactIcon )
                    ||
                    clickableInteraction.type === "Game" && ( GameIcon )
                }
            />
        ))}
    </GoogleMap>
)

const MapInteractions = {
    locations: [
        {
            "id": 1,
            "type": "Quiz",
            "coordinates": [51.523147, -2.577680],
            "description": "Test Me!"
        },
        {
            "id": 2,
            "type": "Fact",
            "coordinates": [51.523160, -2.578440],
            "description": "Learn!"
        },
        {
            "id": 3,
            "type": "Game",
            "coordinates": [51.523531, -2.578289],
            "description": "Let's Play!"
        }
    ]
}

export default MapComponent
