import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import Back from "../backButton/back";
import './quiz.css'
import QuizData from './quizData';

class Quiz extends Component {
    constructor (props) {
        super(props);
        this.state = {
            question: QuizData[0].question,
            qsNo: 0,
            options: quizData[0].options,
            correctAnswer: "Bristol Aerospace Museum",
            flashMsg: ""
        }

        this.handleNavigation = this.handleNavigation.bind(this);

    }

    handleNavigation(event){
        if(event === "next" && (this.state.qsNo < QuizData.length - 1)) {
            console.log(this.state.qsNo + 1);
            this.setState({ qsNo:(this.state.qsNo + 1) }, () => {
                this.setState({
                    question: QuizData[this.state.qsNo].question,
                    options: QuizData[this.state.qsNo].options,
                    correctAnswer: QuizData[this.state.qsNo].correct
                })
            });
        }
    }

    render () {
        return (
            <div className="quiz-container">
                <Back />

                <h3>Question for the quiz goes here</h3>

                <div className="answer-container">
                    <button className="answer-btn" type="radio">Correct Answer</button>
                    <button className="answer-btn">Incorrect Answer 1</button>
                    <button className="answer-btn">Incorrect Answer 2</button>
                    <button className="answer-btn">Incorrect Answer 3</button>

                </div>
            </div>
        );
    }
}

export default Quiz;
