import React, {Component} from 'react';
import {Link} from "react-router-dom";
import './character.css';
import AlfieIcon from "../../images/alfie.svg";
import AmeliaIcon from "../../images/amelia.svg";

import AlfieUnselected from './assets/alfie-unselected.svg'; import AlfieSelected from './assets/alfie-selected.svg';
import AmeliaUnselected from './assets/amelia-unselected.svg'; import AmeliaSelected from './assets/amelia-selected.svg';
import Back from "../../components/backButton/back";

class Character extends Component {
    state = {
        AlfieSelected: false,
        AmeliaSelected: false
    }

    selectAlfie() {
        this.setState({
            AlfieSelected: true,
            AmeliaSelected: false
        })
    }
    selectAmelia() {
        this.setState({
            AlfieSelected: false,
            AmeliaSelected: true
        })
    }

    render () {
        return (
            <div className="container char-page-container"> {/* Page Container */}
                <Back /> {/* Back Button */}

                {/* Inner Container */}
                <div className="inner-container">
                    <h2>Choose Character</h2> {/* Title */}
                    {/* Characters Container */}

                    { AlfieSelected === false &&

                        <div className="character-container">
                            <button className="character-btn alfie">
                                <img src={ AlfieIcon } alt=""/>
                            </button>
                            <button className="character-btn amelia">
                                <img src={ AmeliaIcon } alt=""/>
                            </button>
                        </div>
                    }


                    {/* End of Characters Section */ }



                </div>
                {/* End of Inner Container */}

                {/* Next Button Container */}
                <div className="next-button-container">
                    <Link
                        to="/ready"
                        className="next-button"
                    >
                        <h3>Next</h3>
                    </Link>
                </div>
                {/* Next Button Container */}
            </div>
        );
    }
}

export default Character;
