import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import './gameMenu.css';

import HeaderImg from './assets/char-stats-bg.svg';
import ProfilePic from './assets/user-icon.svg';
import SettingsIcon from './assets/settings-icon.svg';

class GameMenu extends Component {
    render () {
        return (
            <div className="container game-menu-container">
                <div className="stat-header">
                    <img src={HeaderImg} alt="" className="stat-header-background"/>

                    <img src={ProfilePic} alt="" />

                    {/* XP & Score Section */}
                    <div className="user-stats-container">
                        <h3>0 Points</h3>
                    </div>
                    {/* End of XP & Score Section */}

                    <img src={SettingsIcon} alt="" />

                </div>
                <div className="inner-container game-menu-inner-container">
                    {/* GenericQuiz Link Section */}
                    <Link
                        to="/quiz"
                        className="basic-btn quiz-btn"
                    >
                        <h3>Quiz</h3>
                    </Link>
                    <Link
                        to="/game"
                        className="basic-btn game-btn"
                    >
                        <h3>Game</h3>
                    </Link>
                    <Link
                        to="/fact"
                        className="basic-btn fact-btn"
                    >
                        <h3>Fact</h3>
                    </Link>
                    {/* End of GenericQuiz Link Section */}
                </div>
            </div>
        );
    }
}

export default GameMenu;
