import React from "react";

function SectionOnePic() {
    if(navigator.mediaDevices.getUserMedia !== null) {
        const options = {
            video: true,
            audio: true
        };

        navigator.getUserMedia(options, function (stream) {
            const vid = document.getElementById("vid")
            vid.srcObject = stream;
        })
    }

    return(
        <video id="vid"></video>
    )
}
