import React, { Component, useState } from 'react'
import {withScriptjs, withGoogleMap, GoogleMap, Marker, InfoWindow} from 'react-google-maps'
import QuizIcon from "../map/assets/quiz-icon.svg";
import FactIcon from "../map/assets/picture-icon.svg";
import GameIcon from "../map/assets/game-icon.svg";
import {compose, withProps} from "recompose";
import {Link} from "react-router-dom";

const MapInteractions = {
    locations: [
        {
            "id": 1,
            "type": "Quiz",
            "coordinates": [51.523147, -2.577680],
            "description": "Test Me!"
        },
        {
            "id": 2,
            "type": "Fact",
            "coordinates": [51.523160, -2.578440],
            "description": "Learn!"
        },
        {
            "id": 3,
            "type": "Game",
            "coordinates": [51.523531, -2.578289],
            "description": "Let's Play!"
        }
    ]
}

const getLocation = () => {
    if(navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
            position => {
                console.log(position.coords);
                this.setState(prevState => ({
                    currentLatLng: {
                        ...prevState.currentLatLng,
                        lat: position.coords.latitude,
                        lng: position.coords.longitude
                    }
                }))
            }
        )
    } else {
        console.log("error")
    }
}

function UnwrappedMap() {

    const [selectedInteraction, setSelectedInteraction] = useState(null);

    return(
        <GoogleMap
            defaultZoom={25}
            center={{lat: 51.523214, lng: -2.578499}}
        >
            {/* Set Location Markers */}
            {MapInteractions.locations.map((clickInteractions) => (
                <Marker
                    key={clickInteractions.id}
                    position={{
                        lat: clickInteractions.coordinates[0],
                        lng: clickInteractions.coordinates[1]
                    }}
                    icon={
                        clickInteractions.type === "Quiz" && (QuizIcon)
                        ||
                        clickInteractions.type === "Fact" && (FactIcon)
                        ||
                        clickInteractions.type === "Game" && (GameIcon)
                    }
                />
            ))}

            {/* Selected Interaction Display */}
            {
                // Quiz Button
                selectedInteraction === MapInteractions.locations[0] && (
                    <div className="map-info-window">
                        <InfoWindow
                            position={{
                                lat: selectedInteraction.coordinates[0] + 0.000065,
                                lng: selectedInteraction.coordinates[1]
                            }}
                        >
                            <div className="map-button">
                                <h2>{ selectedInteraction.type }</h2>
                                <Link to="/quiz" className="map-basic-btn map-quiz-btn">
                                    <h3>{ selectedInteraction.description }</h3>
                                </Link>
                            </div>
                        </InfoWindow>
                    </div>
                ) ||
                // Fact Button
                selectedInteraction === MapInteractions.locations[1] && (
                    <div className="map-info-window">
                        <InfoWindow
                            position={{
                                lat: selectedInteraction.coordinates[0] + 0.000065,
                                lng: selectedInteraction.coordinates[1]
                            }}
                        >
                            <div className="map-button">
                                <h2>{ selectedInteraction.type }</h2>
                                <Link to="/quiz" className="map-basic-btn fact-btn">
                                    <h3>{ selectedInteraction.description }</h3>
                                </Link>
                            </div>
                        </InfoWindow>
                    </div>
                ) ||
                // Game Button
                selectedInteraction === MapInteractions.locations[2] && (
                    <div className="map-info-window">
                        <InfoWindow
                            position={{
                                lat: selectedInteraction.coordinates[0] + 0.000065,
                                lng: selectedInteraction.coordinates[1]
                            }}
                        >
                            <div className="map-button">
                                <h2>{ selectedInteraction.type }</h2>
                                <Link to="/quiz" className="map-basic-btn game-btn">
                                    <h3>{ selectedInteraction.description }</h3>
                                </Link>
                            </div>
                        </InfoWindow>
                    </div>
                )
            }
        </GoogleMap>
    )
}

const WrappedMap = withScriptjs(withGoogleMap(UnwrappedMap));

export default function Map () {
    return(
        <div style={{width: '100%', height: '100%'}}>
            <WrappedMap
                googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=${process.env.REACT_APP_GOOGLE_KEY}`}
                loadingElement={ <div style={{height: '100%'}} /> }
                containerElement={ <div style={{height: '100%'}} />}
                mapElement={ <div style={{height: '100%'}} />}
            />
        </div>
    )
}

