import React, {Component} from 'react';
import './quizStructure.css';
import { Link } from 'react-router-dom';

import Back from "../../backButton/back";
import HintButton from '../assets/hint-button.svg';
import UserIcon from '../assets/user-icon.svg';

// Question Text/Answer Button Component
class Question extends Component {
    constructor(props){
        super(props);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        e.preventDefault(); //Prevents full refresh
        this.props.onChoiceChange(e.target.value);
    }

    render(){
        const question = this.props.question; // Variable set to equal question from props

        return(
            <div>
                <div className="question-component">
                    <h3>{question.text}</h3>
                </div>
                <div className="answer-container">
                    { question.choices.map(choice => {
                        return(
                                <button
                                    type="submit"
                                    onClick={this.handleChange}
                                    name={question.id}
                                    value={choice.id}
                                    className="answer-btn"
                                >
                                    {choice.text}
                                </button>
                        )
                    })}
                    <img
                        src={HintButton}
                        alt=""
                        className="hint-btn"
                    />
                </div>
            </div>
        )
    }
}
// End of Question Text/Answer Button Component


// Results Component
class Scorebox extends Component {
    render(){
        return(
            <div className="well">
                Question {this.props.current} out of {this.props.total}
                <span className="pull-right">
                    <strong>Score: {this.props.score}</strong>
                </span>
            </div>
        )
    }
}

class Results extends Component {
    constructor (props) {
        super(props);
        this.state = {
            overallScore: 0
        }
    }

    overallScore = () => {
        this.setState((prevState, props) => ({
            overallScore: prevState.overallScore + props.score
        }))
    }


    render(){
        var message = 'You passed the quiz!';
        var link = <Link to="/game-menu">Back to Menu</Link>


        return(
            <div className="results-absolute">
                <div className="results-container">
                    <div className="results-cont results-img">
                        <img src={UserIcon} alt=""/>
                    </div>

                    <div className="results-cont results-text">
                        <h3>+{this.state.overallScore} points!</h3>
                        <button onClick={this.overallScore}>Update Score</button>
                        <h4>Congratulations!</h4>
                    </div>

                    <div className="results-cont results-link">
                        <Link
                            to="/game-menu"
                            className="quiz-return-btn"
                            onClick={this.overallScore}
                        >
                            <h4>OK</h4>
                        </Link>
                    </div>
                </div>
            </div>
        )
    }
}
// End of Results Component

// Final Quiz Format Section
export default class QuizFormat extends Component {
    constructor (props) {
        super(props);
        this.state = {
            score: 0,
            current: 1,
            finalScore: 0
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(choice){
        this.setState((prevState, props, state) => ({
            current: prevState.current + 1,
            score: choice === props.questions[prevState.current - 1].correct ? prevState.score + 1 : prevState.score,
            finalScore: state.finalScore + state.score
        }));
        console.log("Your current score for this game is: " + this.state.score)
    }

    render () {
        const questions = this.props.questions;

        return (
            <div className="quiz-container">
                <Back id="backButton"/>
                {this.state.current > questions.length &&
                    <Results
                        total={questions.length}
                        score={this.state.finalScore}
                    />
                }

                {this.state.current <= questions.length &&
                    <Scorebox
                        total={questions.length}
                        current={this.state.current}
                        score={this.state.score}
                    />
                }

                {this.state.current <= questions.length &&
                    <Question
                        question={questions[this.state.current - 1]}
                        onChoiceChange={this.handleChange}
                    />
                }

            </div>
        );
    }
}
// End of Final Quiz Format Section
