import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import "./welcome.css";

class Welcome extends Component {
    render () {
        return (
            <div className="welcome-container">
                <div className="welcome-title">
                    <h1>Discover and Play</h1>
                    <h2>Explore the Museum in a whole new way</h2>
                    <label
                        for="email-input"
                        className="welcome-label"
                    >
                        E-mail</label>
                    <input
                        type="text"
                        id="email-input"
                        placeholder="Type your email here"
                    />
                    <p>Input your email if you would like to be added to our marketing list and to participate in future prize raffles.</p>
                </div>
                <Link to="/info">
                    <button className="submit-btn" type="submit">Take Me On Board!</button>
                </Link>
            </div>
        );
    }
}

export default Welcome;
