import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import './playerInfo.css';

import BackImg from '../../images/left-close.svg';

import Easy from '../../images/lavel-inactive-easy.svg';
import Medium from '../../images/lavel-inactive-medium.svg';
import Hard from '../../images/level-inactive-hard.svg';
import Settings from '../../images/settings-icon.svg';
import Back from "../../components/backButton/back";

class PlayerInfo extends Component {
    render () {
        return (
            <div className="container p-info-container"> {/* Page Container */}
                <Back />
                <h2>Player Settings</h2>
                <div> {/* Name Input Section */}
                    <label
                        for="name-input"
                        className="name-label"
                    >
                        Name</label>
                    <input
                        type="text"
                        id="name-input"
                        placeholder="Type your name here"
                    />
                </div> {/* End of Name Input Section */}

                <div> {/* Difficulty Options Section */}
                    <h3>Select your difficulty level</h3>
                    <div className="diff-btn-container"> {/* Difficulty Buttons */}
                        <button className="diff-btn easy">
                            <img src={Easy}/>
                        </button>
                        <button className="diff-btn medium">
                            <img src={Medium}/>
                        </button>
                        <button className="diff-btn hard">
                            <img src={Hard}/>
                        </button>
                    </div> {/* End of Difficulty Buttons */}
                    <div className="accessibility-cont"> {/* Accessibility Button Section */}
                        <Link
                            to="/accessibility"
                            className="accessibility-btn"
                        >
                            <img src={Settings} alt="" />
                            <h3>Accessibility Settings</h3>
                        </Link>
                    </div> {/* End of Accessibility Button Section */}
                </div> {/* End of Difficulty Options Section */}
                    <Link
                        to="/character"
                        className="p-info-next"
                    >
                        Next
                    </Link>
            </div>
        );
    }
}

export default PlayerInfo;
