import React, {Component} from 'react';
import {Link} from "react-router-dom";
import './character.css';

import Back from "../../components/backButton/back";
import NextButton from "../../images/next-button.svg";

import AlfieUnselected from './assets/alfie-unselected.svg'; import AlfieSelected from './assets/alfie-selected.svg';
import AmeliaUnselected from './assets/amelia-unselected.svg'; import AmeliaSelected from './assets/amelia-selected.svg';

class Character extends Component {
    state = {
        AlfieSelected: false,
        AmeliaSelected: false
    }

    selectAlfie() {
        this.setState({
            AlfieSelected: true,
            AmeliaSelected: false
        })
    }
    selectAmelia() {
        this.setState({
            AlfieSelected: false,
            AmeliaSelected: true
        })
    }

    render () {

        const { AlfieSelected } = this.state;
        const { AmeliaSelected } = this.state;

        return (
            <div className="container char-page-container"> {/* Page Container */}

                {/* Back Button */}
                <div className="back-button">
                    <Back className="back-button"/>
                </div>

                <div className="c-p-inner-container">

                    {/* Title Section */}
                    <div className="c-p-title-container">
                        Choose Character
                    </div>

                    {/* Characters Container */}
                    { !AlfieSelected && !AmeliaSelected &&
                        <div className="character-container">
                            <button
                                className="character-btn alfie"
                                onClick={() => this.selectAlfie()}
                            >
                                <img src={ AlfieUnselected } alt=""/>
                            </button>
                            <button
                                className="character-btn amelia"
                                onClick={() => this.selectAmelia()}
                            >
                                <img src={ AmeliaUnselected } alt=""/>
                            </button>
                        </div>
                    }

                    { AlfieSelected && !AmeliaSelected &&
                        <div className="character-container">
                            <button className="character-btn alfie">
                                <img src={ AlfieSelectedIcon } alt=""/>
                            </button>
                            <button
                                className="character-btn amelia"
                                onClick={() => this.selectAmelia()}
                            >
                                <img src={ AmeliaUnselected } alt=""/>
                            </button>
                        </div>
                    }

                    { !AlfieSelected && AmeliaSelected &&
                        <div className="character-container">
                            <button
                                className="character-btn alfie"
                                onClick={() => this.selectAlfie()}
                            >
                                <img src={ AlfieUnselected } alt=""/>
                            </button>
                            <button className="character-btn amelia">
                                <img src={ AmeliaSelectedIcon } alt=""/>
                            </button>
                        </div>
                    }

                </div>
                {/* End of Inner Container */}

                {/* Next Button */}
                <div className="next-button-container">
                    <Link to="/ready" className="next-button">
                        <img src={NextButton} alt=""/>
                    </Link>
                </div>
            </div>
        );
    }
}

export default Character;
