import React, { Component, useState, useEffect } from 'react'
import {withScriptjs, withGoogleMap, GoogleMap, Marker, InfoWindow} from 'react-google-maps'
import QuizIcon from "../map/assets/quiz-icon.svg";
import FactIcon from "../map/assets/picture-icon.svg";
import GameIcon from "../map/assets/game-icon.svg";
import {compose, withProps} from "recompose";
import {Link} from "react-router-dom";

const MapInteractions = {
    locations: [
        {
            "id": 1,
            "type": "Quiz",
            "coordinates": [51.523147, -2.577680],
            "description": "Test Me!"
        },
        {
            "id": 2,
            "type": "Fact",
            "coordinates": [51.523160, -2.578440],
            "description": "Learn!"
        },
        {
            "id": 3,
            "type": "Game",
            "coordinates": [51.523531, -2.578289],
            "description": "Let's Play!"
        }
    ]
}

const getLocation = () => {
    if(navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
            position => {
                console.log(position.coords);
                this.setState(prevState => ({
                    currentLatLng: {
                        ...prevState.currentLatLng,
                        lat: position.coords.latitude,
                        lng: position.coords.longitude
                    }
                }))
            }
        )
    } else {
        console.log("error")
    }
}

class UnwrapMap extends Component {
    constructor (props) {
        super(props);
        this.state = {
            currentLocation: { latitude: null, longitude: null},
            isMarkerShown: false
        }
    }

    componentWillUpdate () {
        this.getLocation()
    }
    componentDidMount () {
        this.delayedShowMarker()
    }

    // Parameter for showing marker for the user's location
    delayedShowMarker = () => {
        setTimeout(() => {
            this.getLocation()
            this.setState({ isMarkerShown: true })
        }, 3000)
    }

    // Parameter for getting user's current location (via coordinates)
    getLocation = () => {
        if(navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    console.log(position.coords);
                    this.setState(prevState => ({
                        currentLocation: {
                            ...prevState.currentLocation,
                            latitude: position.coords.latitude,
                            longitude: position.coords.longitude
                        }
                    }))
                }
            )
        } else {
            console.log("error")
        }
    }

    render () {
        return(
            <GoogleMap
                defaultZoom={25}
                defaultCenter={{ lat: this.state.currentLocation.latitude, lng: this.state.currentLocation.longitude  }}
            >
                {this.state.isMarkerShown &&
                    <Marker
                        position={{ lat: this.state.currentLocation.latitude, lng: this.state.currentLocation.longitude }}
                    />
                    }
                { MapInteractions.locations.map((clickInteractions) => (
                    <Marker
                        key={clickInteractions.id}
                        position={{
                            lat: clickInteractions.coordinates[0],
                            lng: clickInteractions.coordinates[1]
                        }}
                        icon={
                            clickInteractions.type === "Quiz" && (QuizIcon)
                            ||
                            clickInteractions.type === "Fact" && (FactIcon)
                            ||
                            clickInteractions.type === "Game" && (GameIcon)
                        }
                    />
                ))}
            </GoogleMap>
        )
    }
}

const WrapMap = withScriptjs(withGoogleMap(UnwrapMap));

