import React, {Component} from 'react';
import { Map, GoogleApiWrapper, Marker } from "google-maps-react";
import Icon from '../map/assets/quiz-icon.svg';

const mapStyles = {
    width: '100%',
    height: '100%',
};

// Impromptu JSON data for locations
const Interaction = {
    locations: [
        {
            "id": 1,
            "type": "Quiz",
            "coordinates": [51.523147, -2.577680],
            "description": "Test Me!"
        },
        {
            "id": 2,
            "type": "Fact",
            "coordinates": [51.523160, -2.578440],
            "description": "Learn!"
        },
        {
            "id": 3,
            "type": "Game",
            "coordinates": [51.523531, -2.578289],
            "description": "Let's Play!"
        }
    ]
}

class MapTest extends Component {

    constructor (props) {
        super(props);
        this.state = {
            currentLatLng: {
                latitude: null,
                longitude: null,
            },
            isMarkerShow: false
        };
        // this.getLocation = this.getLocation.bind(this);
        // this.getCoordinates = this.getCoordinates.bind(this);
    }

    componentWillUpdate () {
        this.getGeoLocation()
    }

    componentDidMount () {
        this.delayedShowMarker()
    }

    delayedShowMarker = () => {
        setTimeout(() => {
            this.getGeoLocation()
            this.setState({ isMarkerShown: true })
        }, 5000)
    }
    handleMarkerClick = () => {
        this.setState({ isMarkerShow: false})
        this.delayedShowMarker()
    }

    // getLocation() {
    //     if(navigator.geolocation) {
    //         navigator.geolocation.watchPosition(this.getCoordinates, this.handleLocationError);
    //
    //         navigator.geolocation.getCurrentPosition(function (position) {
    //             console.log(position)
    //         })
    //
    //     } else {
    //         alert("Geolocation not supported by this browser");
    //     }
    // }

    getGeoLocation = () => {
        if(navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(position => {
                console.log(position.coords);
                this.setState(prevState => ({
                    currentLatLng: {
                        ...prevState.currentLatLng,
                        latitude: position.coords.latitude,
                        longitude: position.coords.longitude
                    }
                }))
            })
        } else {
            console.log("Error")
        }
    }

    getCoordinates(position) {
        this.setState({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
        })
    }

    //Error Handling
    handleLocationError(error) {
        switch(error.code){
            case error.PERMISSION_DENIED:
                alert("Denied request for Geolocation")
                break;
            case error.POSITION_UNAVAILABLE:
                alert("Location info is unavailable")
                break;
            case error.TIMEOUT:
                alert("Location request has timed out")
                break;
            case error.UNKNOWN_ERR:
                alert("An unknown error occurred")
                break;
            default:
                alert("An unknown error occurred")
        }
    }

    render () {
        return (
            <div>
                <button onClick={this.getLocation}>Location</button>
                <h2>Lon: { this.state.longitude }, Lat: {this.state.latitude}</h2>
                <Map
                    google={this.props.google}
                    zoom={14}
                    style={mapStyles}
                    initialCenter={{
                        lat: this.state.latitude,
                        lng: this.state.longitude
                    }}
                />
                <Marker
                    position={{
                        lat: this.state.latitude,
                        lng: this.state.longitude
                    }}
                    icon={Icon}
                />
            </div>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyC-AmFN95fVGvNGJrQULEoHAX-1BYkk7xg'
})(MapTest);
