import React, {Component} from 'react'; import './recap.css';
import CloseButton from "../../components/quiz/assets/16/icon.png";
import BackdropBubble from "../../components/quiz/assets/backdrop-bubble.png";
import HintButton from "../../components/quiz/assets/hint-button.png";
import axios from "axios";
import UserIcon from "../../components/quiz/assets/user-icon.svg";
import {Link} from "react-router-dom";
import Back from "../../components/backButton/back";
import CameraImg from "../../components/quiz/quizzes/sectionTwo/assets/camera.svg";
import CowImg from "../../components/quiz/quizzes/sectionTwo/assets/cow.svg";
import BulbImg from "../../components/quiz/assets/lightbulb.svg";
import KiteImg from "../../components/quiz/quizzes/sectionOne/assets/kites.svg";
import PyramidImg from "../../components/quiz/quizzes/sectionOne/assets/pyramid.svg";

// Question Screen
class RecapQuestion extends Component {
    constructor(props){
        super(props);
        this.state = {
            hintPopup: false,
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        e.preventDefault(); //Prevents full refresh
        this.props.onChoiceChange(e.target.value);
    }

    render(){
        const question = this.props.question; // Variable set to equal question from props

        return(
            <div>

                {/* Question Layout */}
                <div className="question-component">
                    {question.text}
                </div>
                <div className="answer-container">
                    { question.choices.map(choice => {
                        return(
                            <button
                                type="submit"
                                onClick={this.handleChange}
                                name={question.id}
                                value={choice.id}
                                className="answer-btn"
                            >
                                {choice.text}
                            </button>
                        )
                    })}
                </div>
            </div>
        )
    }
}

// Results Screen
class RecapResults extends Component {
    constructor (props) {
        super(props);
        this.state = {
            overallScore: 0,
            usersDetails: ''
        }
    }

    overallScore = () => {
        this.setState((prevState, props) => ({
            overallScore: (prevState.overallScore + props.score)
        }))
    }

    handleSubmit = (event) => {

        const payload = {
            score: this.props.score
        }

        axios({
            url: 'api/save',
            method: 'POST',
            data: payload
        })
            .then(() => {
                console.log('Data sent to server')
            })
            .catch(() => {
                console.log('ISE: Error sending data to server')
            })
    }

    componentDidMount () {
        this.overallScore()

        axios.get('api/')
            .then(res => {
                console.log(res.data)
            })
            .catch(error => {
                console.log(error, 'Internal Server Error')
            })
    }

    render(){
        return(
            <div className="results-absolute">
                <div className="results-container">
                    <div className="results-cont results-img">
                        <img src={UserIcon} alt=""/>
                    </div>

                    <div className="results-cont results-text">
                        <h3>+{this.state.overallScore} points!</h3>
                        <h4>Congratulations!</h4>
                    </div>

                    <div className="results-cont results-link">
                        <Link
                            to="/game-menu"
                            className="quiz-return-btn"
                            onClick={this.handleSubmit}
                        >
                            <h4>OK</h4>
                        </Link>
                    </div>
                </div>
            </div>
        )
    }
}

// Recap Section
class Recap extends Component {
    constructor (props) {
        super(props);
        this.state = {
            score: 0,
            current: 1,
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(choice){
        this.setState((prevState, props) => ({
            current: prevState.current + 1,
            score: choice === props.questions[prevState.current - 1].correct ? prevState.score + 50 : prevState.score,
        }));
    }

    render () {
        const questions = this.props.questions;
        const randomQuestion = questions[Math.floor(Math.random() * questions.length )]

        return (
            <div className="quiz-container">
                <Back id="backButton"/>

                {/* Results */}
                {this.state.current > 3 &&
                    <RecapResults
                        total={questions.length}
                        score={this.state.score}
                        overallScore={this.state.overallScore}
                    />
                }

                {/* Question */}
                {this.state.current <= 3 &&
                    <RecapQuestion
                        question={randomQuestion}
                        onChoiceChange={this.handleChange}
                    />
                }

            </div>
        );
    }
}

