import React, {Component} from 'react';
import GoogleMap from "google-map-react";

const mapStyles = {
    height: '100%',
    width: '100%'
}

const markerStyle = {
    height: '50px',
    width: '50px',
    marginTop: '-50px'
}

const imgStyle = {
    height: '100%'
}

const Marker = ({ title }) => (
    <div style={markerStyle}>
        <img style={imgStyle} src="https://res.cloudinary.com/og-tech/image/upload/s--OpSJXuvZ--/v1545236805/map-marker_hfipes.png" alt='' />
        <h3>{title}</h3>
    </div>
);

class MapTest extends Component {
    render () {
        return (
            <div>
                <GoogleMap
                    style={mapStyles}
                    bootstrapURLKeys={{ key: 'AIzaSyC-AmFN95fVGvNGJrQULEoHAX-1BYkk7xg' }}
                    center={{ lat: 5.6219868, lng: -0.1733074 }}
                    zoom={14}
                >
                    <Marker
                        title={'Current Location'}
                        lat={5.6219868}
                        lng={-0.1733074}
                    />
                </GoogleMap>
            </div>
        );
    }
}

export default MapTest;
