import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import Back from "../backButton/back";
import './quiz.css'

const quizData = [
    {
        key: "1",
        question: "What is the museum called?",
        options: {
            answer1: "Bristol Museum",
            answer2: "Museum of Flying",
            answer3: "Bristol Aerospace Museum",
            answer4: "Bristolian Flying House"
        },
        correct: "Bristol Aerospace Museum"
    },
    {
        key: "2",
        question: "What are the foxes called?",
        options: {
            answer1: "Archie and Amy",
            answer2: "Alfie and Amelia",
            answer3: "Alex and Emily",
            answer4: "Alan and Aimee"
        },
        correct: "Alfie and Amelia"
    },
    {
        key: "3",
        question: "What is the museum called 2?",
        options: {
            answer1: "Bristol Museum",
            answer2: "Museum of Flying",
            answer3: "Bristol Aerospace Museum",
            answer4: "Bristolian Flying House"
        },
        correct: "Bristol Aerospace Museum"
    },
    {
        key: "4",
        question: "What are the foxes called 2?",
        options: {
            answer1: "Archie and Amy",
            answer2: "Alfie and Amelia",
            answer3: "Alex and Emily",
            answer4: "Alan and Aimee"
        },
        correct: "Alfie and Amelia"
    },
]

class Popup extends Component {
    render(){
        return(
            <div className="popup">
                <div className="popup-inner">
                    <h1>{this.props.text}</h1>
                    <button onClick={this.props.closePopup}>Close</button>
                </div>
            </div>
        )
    }
}

class Quiz extends Component {
    constructor (props) {
        super(props);
        this.state = {
            question: quizData[0].question,
            qsNo: 0,
            options: quizData[0].options,
            correctAnswer: "Bristol Aerospace Museum",
            correctCount: 0,
            incorrectCount: 0,
            flashMsg: "",
            score: 0,
            showPopup: true
        }

        this.handleNavigation = this.handleNavigation.bind(this);
        this.handleAnswerClick = this.handleAnswerClick.bind(this);

    }

    // Function for when the user presses next/previous button
    handleNavigation(event){
        // Event for when the user presses the next button
        if(event === "next" && (this.state.qsNo < quizData.length - 1)) {
            console.log(this.state.qsNo + 1);
            this.setState({ qsNo:(this.state.qsNo + 1) }, () => {
                this.setState({
                    question: quizData[this.state.qsNo].question,
                    options: quizData[this.state.qsNo].options,
                    correctAnswer: quizData[this.state.qsNo].correct
                })
            });
        } else if(event === "next" && (this.state.qsNo >= quizData.length - 1)) {
            this.setState({
                score: this.state.correctCount
            })
            console.log("Game Over! Your score is " + this.state.correctCount * 50)

        }

        // Event for when the user presses the previous button
        if(event === "previous" && (this.state.qsNo >= 1)) {
            console.log("prvs");
            this.setState({ qsNo:(this.state.qsNo - 1)}, () => {
                this.setState({
                    question: quizData[this.state.qsNo].question,
                    options: quizData[this.state.qsNo].options
                })
            });
        }
    }

    // Function for when the user presses one of the answer buttons
    handleAnswerClick(option) {
        // Causes the next question to pop up
        // this.handleNavigation("next");
        console.log(option); // Logs the answer that the user selects

        if(this.state.correctAnswer === option){
            console.log("Correct");
            this.setState({correctCount: this.state.correctCount + 1})
        } else {
            this.setState({incorrectCount: this.state.incorrectCount + 1})
        }
    }

    render () {
        return (
            <div className="quiz-container">
                <Back />

                <Question questionData={this.state.question} />
                <Answers
                    options={this.state.options}
                    onAnswerClick={this.handleAnswerClick}
                />
                <Results
                    correctCount={this.state.correctCount}
                    incorrectCount={this.state.incorrectCount}
                />
                <Navigation onClickFromParent={this.handleNavigation}/>
            </div>
        );
    }
}

const Question = (props) => {
    return(
        <div>
            <div className="question-component">
                <h3>{props.questionData}</h3>
            </div>
        </div>
    )
}

const Answers = (props) => {

    function passOption(e) {
        props.onAnswerClick(e);
    }

    return(
        <div className="answer-container">
            <button
                className="answer-btn"
                onClick={() => { passOption(props.options.answer1) }}>{props.options.answer1}
            </button>
            <button
                className="answer-btn"
                onClick={() => { passOption(props.options.answer2) }}>{props.options.answer2}
            </button>
            <button
                className="answer-btn"
                onClick={() => { passOption(props.options.answer3) }}>{props.options.answer3}
            </button>
            <button
                className="answer-btn"
                onClick={() => { passOption(props.options.answer4) }}>{props.options.answer4}
            </button>
        </div>
    )
}

const Navigation = (props) => {
    const clickNext = () => {
        return(props.onClickFromParent("next"));
    }

    return(
        <div>
            <button
                className="p-info-next"
                onClick={clickNext}
            >
                Next
            </button>
        </div>
    )
}

const Results = (props) => {
        return(
            <div>
                <h4>Score: {props.correctCount * 50}</h4>
                Correct: {props.correctCount} | Incorrect: {props.incorrectCount}
            </div>
        )
}

// const Popup = (props) => {
//     return(
//         <div className="popup">
//             Hello
//         </div>
//     )
// }

export default Quiz;
