                    </div>

                    {/* Experience Bar */}
                    <div className="w-m-h-score-container">

                        {/* Text */}
                        <div className="wmhs-title">
                            0 points
                        </div>

                        {/* Completion Bar */}
                        <div className="wmhs-bar">

                        </div>

                    </div>

                    {/* Settings Icon */}
