import React, {Component} from "react";
import FactFormat from "../factFormat";

const Facts = [
    {
        options: [
            {
                id: 1,
                text: 'Britain knew the war was coming and warned important industries. BAC started moving its workers to secret places across Bristol a month before the war officially began.',
                number: 'Fact 1 of 4',
            },
        ],
    },
    {
        options: [
            {
                id: 2,
                text: 'Why are people designing planes in a swimming pool?\n' +
                    '\n' +
                    'During WWII Germany tried to bomb important factories around the UK. To protect their workers, companies like Bristol, sent lots of them to work in secret and unexpected places around the city instead of in the main factory.',
                number: 'Fact 2 of 4',
            },
        ]
    },
    {
        options: [
            {
                id: 3,
                number: 'Fact 3 of 4',
                text: 'The controls for the Boxkite were very simple. A stick to move the elevators on the wings and a pedal to move the rudders on the tail left and right.',
            },
        ]
    },
    {
        options: [
            {
                id: 4,
                number: 'Fact 4 of 4',
                text: 'Bristol Boxkite worked by producing lift and drag forces with its wings. Just like kites you can fly in the air!',
            },
        ]
    },
]

class SectionFourFact extends Component {
    render () {
        return(
            <div>
                <FactFormat facts={Facts} />
            </div>
        )
    }
}

export default SectionFourFact;
