import React, { useState } from 'react';
import './map.css';

import GoogleMapReact from 'google-map-react';
import {
    GoogleMap,
    withScriptjs,
    withGoogleMap,
    Marker,
    InfoWindow
} from 'react-google-maps';

import * as InteractionData from './mapData.json';

import { Icon } from '@iconify/react';
import locationIcon from '@iconify/icons-mdi/map-marker'

// const LocationPin = ({ text }) => (
//     <div className="pin">
//         <Icon icon={locationIcon} className="pin-icon"/>
//         <p className="pin-text">{text}</p>
//     </div>
// )
//
// const Map = ({ location, zoomLevel }) => (
//     <div className="map">
//     </div>
// )

// Impromptu JSON data for locations
const Interaction = {
    locations: [
        {
            "id": 1,
            "type": "Quiz",
            "coordinates": [51.523147, -2.577680]
        },
        {
            "id": 2,
            "type": "Fact",
            "coordinates": [51.523160, -2.578440]
        },
        {
            "id": 3,
            "type": "Game",
            "coordinates": [51.523531, -2.578289]
        }
    ]
}

function UnwrappedMap() {

    const [selectedInteraction, setSelectedInteraction] = useState(null);

    return (
        <GoogleMap
            defaultZoom={20}
            defaultCenter={{lat: 51.523214, lng: -2.578207}}
        >
            {Interaction.locations.map((interactions) => (
                <Marker
                    key={interactions.id}
                    position={{
                        lat: interactions.coordinates[0],
                        lng: interactions.coordinates[1]
                    }}
                    onClick={() => {
                        setSelectedInteraction(interactions);
                    }}
                />
            ))}
            {selectedInteraction && (
                <InfoWindow
                    position={{
                        lat: selectedInteraction.coordinates[0],
                        lng: selectedInteraction.coordinates[1]
                    }}
                    onCloseClick={() => {
                        setSelectedInteraction(null);
                    }}
                >
                    <h2>{ selectedInteraction.type }</h2>
                    <p>Hello</p>
                </InfoWindow>
            )}
        </GoogleMap>
    )
}

const WrappedMap = withScriptjs(withGoogleMap(UnwrappedMap));

export default function Map() {
    return (
        <div style={{width: '100%', height: '100%', zIndex: '-2'}}>
            <WrappedMap
                googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=${process.env.REACT_APP_GOOGLE_KEY}`}
                loadingElement={ <div style={{height: '100%'}} /> }
                containerElement={ <div style={{height: '100%'}} />}
                mapElement={ <div style={{height: '100%'}} />}
            />
        </div>
    )
}
