import React, { Component, useState } from 'react'
import { withScriptjs, withGoogleMap, GoogleMap, Marker } from 'react-google-maps'
import MapComponent from './mapComponent'
import QuizIcon from "../map/assets/quiz-icon.svg";
import FactIcon from "../map/assets/picture-icon.svg";
import GameIcon from "../map/assets/game-icon.svg";

const [ selectedInteraction, setSelectedInteraction ] = useState(null);

class MapTest extends Component {
    constructor(props){
        super(props)
        this.state = {
            currentLatLng: {
                lat: 0,
                lng: 0
            },
            isMarkerShown: false
        }
    }

    componentWillUpdate(){
        this.getGeoLocation()
    }

    componentDidMount() {
        this.delayedShowMarker()
    }

    delayedShowMarker = () => {
        setTimeout(() => {
            this.getGeoLocation()
            this.setState({ isMarkerShown: true })
        }, 5000)
    }

    handleMarkerClick = () => {
        this.setState({ isMarkerShown: false })
        this.delayedShowMarker()
    }

    getGeoLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    console.log(position.coords);
                    this.setState(prevState => ({
                        currentLatLng: {
                            ...prevState.currentLatLng,
                            lat: position.coords.latitude,
                            lng: position.coords.longitude
                        }
                    }))
                }
            )
        } else {
            console.log("error")
        }
    }

    render() {
        return (
            <MapComponent
                isMarkerShown={this.state.isMarkerShown}
                onMarkerClick={this.handleMarkerClick}
                currentLocation={this.state.currentLatLng}
            >
                {MapInteractions.locations.map((clickableInteraction) => (
                    <Marker
                        key={clickableInteraction.id}
                        position={{
                            lat: clickableInteraction.coordinates[0],
                            lng: clickableInteraction.coordinates[1]
                        }}
                        onClick={() => {
                            setSelectedInteraction(clickableInteraction);
                        }}
                        icon={
                            clickableInteraction.type === "Quiz" && ( QuizIcon )
                            ||
                            clickableInteraction.type === "Fact" && ( FactIcon )
                            ||
                            clickableInteraction.type === "Game" && ( GameIcon )
                        }
                    />
                ))}
            </MapComponent>
        )
    }
}

const MapInteractions = {
    locations: [
        {
            "id": 1,
            "type": "Quiz",
            "coordinates": [51.523147, -2.577680],
            "description": "Test Me!"
        },
        {
            "id": 2,
            "type": "Fact",
            "coordinates": [51.523160, -2.578440],
            "description": "Learn!"
        },
        {
            "id": 3,
            "type": "Game",
            "coordinates": [51.523531, -2.578289],
            "description": "Let's Play!"
        }
    ]
}

export default MapTest;
