import React, {Component} from 'react';
import {Link} from "react-router-dom";
import './ready.css';

import BackImg from "../../images/left-close.svg";
import PlaneVector from '../../images/plane-vector.svg';
import Back from "../../components/backButton/back";

class Ready extends Component {
    render () {
        return (
            <div className="container ready-container">
                <Back />

                <h2>All Set!</h2>
                <p>Get ready to start the adventure and collect as many points as you can</p>


                <img src={PlaneVector} alt=""/>

                <Link
                    to="game-menu"
                    className="basic-btn start-adventure"
                >
                    <h3>Start the Adventure</h3>
                </Link>

                <Link
                    to="/ts-and-cs"
                    className="text-btn terms-btn"
                >
                    <h4>Guide</h4>
                </Link>
            </div>
        );
    }
}

export default Ready;
