import React, {Component} from 'react';
import {Link} from "react-router-dom";
import './accessibility.css';

import BackImg from "../../images/left-close.svg";
import SmallText from "../../images/small-text.svg";
import MediumText from "../../images/medium-text.svg";
import LargeText from "../../images/large-text.svg";
import VoiceOver from "../../images/voiceover-icon.svg";
import SoundIcon from "../../images/voulme-icon.svg";


class Accessibility extends Component {
    render () {
        return (
            <div className="p-info-container"> {/* Page Container */ }
                <Link to="/info"> {/* Back Button */ }
                    <img src={ BackImg } alt=""/>
                </Link> {/* End of Back Button */ }
                <h2>Accessibility Settings</h2>
                <div> {/* Text Size Section */ }
                    <h3>Font Size</h3>
                    <div className="font-size-container">
                        <button className="font-btn small">
                            <img src={SmallText} alt="" />
                        </button>
                        <button className="font-btn medium">
                            <img src={MediumText} alt="" />
                        </button>
                        <button className="font-btn large">
                            <img src={LargeText} alt="" />
                        </button>
                    </div>
                </div>
                {/* End of Text Size Section */ }

                <div> {/* Difficulty Options Section */ }
                    <h3>Select your difficulty level</h3>
                    <div className="diff-btn-container"> {/* Difficulty Buttons */ }
                        <button className="diff-btn easy">
                        </button>
                        <button className="diff-btn medium">
                        </button>
                        <button className="diff-btn hard">
                        </button>
                    </div>
                    {/* End of Difficulty Buttons */ }
                    <div className="accessibility-container"> {/* Accessibility Button Section */ }
                        <Link
                            to="/accessibility"
                            className="accessibility-btn"
                        >
                            <h3>Accessibility Settings</h3>
                        </Link>
                    </div>
                    {/* End of Accessibility Button Section */ }
                </div>
                {/* End of Difficulty Options Section */ }
                <Link
                    to="/info-two"
                    className="p-info-next"
                >
                    Next
                </Link>
            </div>
        );
    }
}

export default Accessibility;
