import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import './gameMenu.css';

import HeaderImg from './assets/char-stats-bg.svg';
import ProfilePic from './assets/user-icon.svg';
import SettingsIcon from './assets/settings-icon.svg';
import MapOverlay from './assets/MainBuidling.png';
import QuestionIcon from './assets/questionIcon.png';

let museumOverlay;

function initMap() {

    const map = new google.maps.Map(document.getElementById("map"),
        {
            zoom: 18,
            center: {
                lat: 51.523,
                lng: -2.578499
            }
        }
    );

    const imageBounds = {
        north: 51.523630,
        east: -2.577550,
        south: 51.522824,
        west: -2.578875,
    };

    museumOverlay = new google.maps.GroundOverlay({MapOverlay}, imageBounds);
    museumOverlay.setMap(map);

    var quizMarker1 = new google.maps.Marker(
        {
            position: {
                lat: 51.523386,
                lng: -2.578324
            },
            map: map,
            icon: QuestionIcon
        }
    );

    var quizLink1 = new google.maps.InfoWindow(
        {
            content: '<h2>Quiz Link</h2>'
        }
    );

    quizMarker1.addEventListener('click', function () {
        window.location = ('quiz.html')
    })

}

