/* global google */
import React, {useState} from 'react';
import './map.css';
import { Link } from 'react-router-dom';

import { usePosition } from 'use-position';

import {
    GoogleMap,
    withScriptjs,
    withGoogleMap,
    Marker,
    InfoWindow,
    GroundOverlay
} from 'react-google-maps';

import GameIcon from './assets/game-icon.svg';
import QuizIcon from './assets/quiz-icon.svg';
import FactIcon from './assets/fact-icon.svg';
import MapOverlay from './assets/main-building.svg';

// Impromptu JSON data for locations
const Interaction = {
    locations: {
        sectionOne: [
            {
                "id": 1,
                "type": "Quiz",
                "coordinates": [51.523406, -2.578008],
                "description": "Test Me!"
            },
            {
                "id": 2,
                "type": "Fact",
                "coordinates": [51.523323, -2.577915],
                "description": "Learn!"
            }
        ],

        sectionTwo: [
            {
                "id": 1,
                "type": "Game",
                "coordinates": [51.523531, -2.578289],
                "description": "Let's Play!"
            }
        ]
    },
}

function UnwrappedMap() {

    const [selectedInteraction, setSelectedInteraction] = useState(null);

    const watch = true;
    const { latitude, longitude } =
        usePosition(watch, {enableHighAccuracy: true});


    return (
        // Creates map
        <GoogleMap
            defaultZoom={20}
            defaultCenter={{lat: latitude, lng: longitude}}
            center={{ lat: 51.523160, lng: -2.578440 }}
            options={{
                fullscreenControl: false,
                zoomControl: false,
                streetViewControl: false,
                mapTypeControl: false,
                // draggable: false,
                clickableIcons: false,
            }}
        >
            <GroundOverlay
                bounds={new google.maps.LatLngBounds(
                    new google.maps.LatLng(51.522800, -2.578910),
                    new google.maps.LatLng(51.523655, -2.577485)
                )}
                url={MapOverlay}
                opacity={1}
            />

            {/* User Location Marker*/}
            <Marker
                position={{ lat: latitude, lng: longitude }}
            />

            {Interaction.locations.map((interaction) => (
                <Marker
                    key={[interaction.id, interaction.id]}
                    position={{
                        lat: interaction.coordinates[0],
                        lng: interaction.coordinates[1]
                    }}
                    onClick={() => {
                        setSelectedInteraction(interaction)
                    }}
                    icon={
                        interaction.type === "Quiz" && (QuizIcon)
                        ||
                        interaction.type === "Fact" && (FactIcon)
                        ||
                        interaction.type === "Game" && (GameIcon)
                    }
                />
            ))}

